HashMap<String, Object> response = exchange.getIn().getBody(Map.class);
response.put("score", (Double)exchange.getProperty("confidence"));	
exchange.getIn().setBody(response);

ArrayList<HashMap<String, Object>> detectResults = (ArrayList)exchange.getIn().getBody();
ArrayList<HashMap<String, Object>> detectResponse = new ArrayList<HashMap<String, Object>>();

ObjectMapper mapper = new ObjectMapper();

if (detectResults != null) {
    for (int i = 0; i < detectResults.size(); i++) {		
        HashMap<String, Object> result = detectResults.get(i);
  			HashMap<String, Object> unitResponse = new HashMap();
				unitResponse.put("provider", "google");
				unitResponse.put("detected_dialect", result.get("code").toLowerCase());
				unitResponse.put("detected_confidence", result.get("confidence"));
				detectResponse.add(unitResponse);
    }
}
exchange.getIn().setBody(mapper.writeValueAsString(detectResponse));

