	String radius = (String)exchange.getProperty("radius").item(0).getTextContent().trim();

	String pattern = "([0-9\\-\\.]*)\s(0-9\\-\\.)*\s?(0-9\\-\\.)?";
	Pattern r = Pattern.compile(pattern);	
	Matcher m = r.matcher(position);
	if (m.find( )) {
		double lat = Double.parseDouble(m.group(0));
		double lng = Double.parseDouble(m.group(1));
		double alt;

		try {
			if (m.group(2) != null) 
				alt = Double.parseDouble(m.group(2));

		} catch (groovy.lang.MissingMethodException e) {}

		HashMap<String, Object> geoRequest = new HashMap<String, Object>() {{
			put("lat", lat);
			put("lng", lng);
			put("latlng_accuracy", radius);
		}};

		if (alt != null)
			geoRequest.put("altitude", alt);

		exchange.getIn().setBody(mapper.writeValueAsString(geoRequest));
		exchange.setProperty("hasLocation", true);
	} 
} catch (Exception e) {
	System.out.println(e.getMessage());
