@BindToRegistry
public Processor redisRpush() {
    return exchange -> {
        String message = (String)exchange.getIn().getBody();
				String redisUri = (String)exchange.getProperty("redisUri");
				//System.out.println("Emitting to Redis broker: " + redisUri);

        if (message != null) {
            Vertx vertx = Vertx.vertx();

            Redis.createClient(vertx, redisUri)
							.connect()
              .onSuccess(conn -> {
								conn.send(Request.cmd(Command.RPUSH).arg("ioevents").arg(message));
								conn.close();
							});
        }
    };
}

@BindToRegistry
public Processor redisPublish() {
    return exchange -> {
				String redisUri = (String)exchange.getProperty("redisUri");
				String channel = (String)exchange.getProperty("channel");			
				String message = (String)exchange.getIn().getBody();

				if (channel == null)
					channel = "ioevent";
				//System.out.println("Emitting to Redis broker: " + redisUri);

        if (message != null) {
            Vertx vertx = Vertx.vertx();

            Redis.createClient(vertx, redisUri)
                .send(Request.cmd(Command.PUBLISH).arg(channel).arg(message));
        }
    };
}

