import org.apache.camel.CamelContext;
import org.apache.camel.Endpoint;
import org.apache.camel.builder.RouteBuilder;
import org.apache.camel.component.sip.SipComponent;
import org.apache.camel.component.sip.SipConfiguration;
import org.apache.camel.component.sip.SipEndpoint;

import java.io.IOException;
import java.net.URI;
import java.util.Map;

public class SipRoute extends RouteBuilder {

  public class SipComponentImpl extends SipComponent {

    public SipComponentImpl() {
    }

    @Override
    protected Endpoint createEndpoint(String uri, String remaining, Map<String, Object> parameters) throws Exception {
      SipConfiguration config = new SipConfiguration();
      config.initialize(new URI(uri), parameters, this);

      SipEndpoint sipEndpoint = new SipEndpoint(uri, this, config);
      setProperties(sipEndpoint, parameters);
      return sipEndpoint;
    }

    @Override
    public void close() throws IOException {
      super.close();
    }
  }


  @Override
  public void configure() throws Exception {
    log.info("***");

    CamelContext context = getContext();
    if (context.hasComponent("sip") == null) {
      SipComponentImpl sipComponent = new SipComponentImpl();
      context.addComponent("sip", sipComponent);
    }

    from("sip://0.0.0.0:5060?stackName=PresenceAgent&presenceAgent=true&eventHeaderName=evtHdrName&eventId=evtid")
     .log("test: ${body}");
  }
}
