@BindToRegistry
public BasicDataSource dsPostgres() {
    CamelContext context = getContext();
    String url = (String)context.getPropertiesComponent().resolveProperty("convey.postgres.uri").get();
    String username = (String)context.getPropertiesComponent().resolveProperty("convey.postgres.user").get();
    String password = (String)context.getPropertiesComponent().resolveProperty("convey.postgres.pass").get();

    final BasicDataSource basicDataSource = new BasicDataSource();
    basicDataSource.setDriverClassName("org.postgresql.Driver");
        basicDataSource.setUrl(url);
    basicDataSource.setUsername(username);
    basicDataSource.setPassword(password);
    basicDataSource.setValidationQuery("SELECT 1");
    basicDataSource.addConnectionProperty("zeroDateTimeBehavior", "CONVERT_TO_NULL");
    return basicDataSource;
}

