public org.apache.activemq.jms.pool.PooledConnectionFactory pooledConnectionFactory() {
    org.apache.activemq.ActiveMQConnectionFactory activeMQConnectionFactory =
            new org.apache.activemq.ActiveMQConnectionFactory();
    activeMQConnectionFactory.setBrokerURL("tcp://localhost:61616");
    activeMQConnectionFactory.setUserName("system");
    activeMQConnectionFactory.setPassword("manager");
    activeMQConnectionFactory.setUseRetroactiveConsumer(false);

    org.apache.activemq.jms.pool.PooledConnectionFactory pooledConnectionFactory =
            new org.apache.activemq.jms.pool.PooledConnectionFactory();
    pooledConnectionFactory.setConnectionFactory(activeMQConnectionFactory);
    pooledConnectionFactory.setMaxConnections(1);
    pooledConnectionFactory.setMaximumActiveSessionPerConnection(500);
    pooledConnectionFactory.setConnectionTimeout(30000)
    pooledConnectionFactory.setIdleTimeout(30000);
    pooledConnectionFactory.setExpiryTimeout(60000L);

    return pooledConnectionFactory;
}
