@BindToRegistry
public RedisSMQ RsmqConnection() {
    CamelContext context = getContext();
    String redisHost = (String)context.getPropertiesComponent().resolveProperty("convey.redis.host").get();
		String environment = (String)context.getPropertiesComponent().resolveProperty("convey.web.environment").get();

		RedisSMQConfig rsmqConfig = new RedisSMQConfig().host(redisHost).ns(environment);
    RedisSMQ rsmq = new RedisSMQ(rsmqConfig);

    return rsmq;
}

@BeanInject("RsmqConnection") 
RedisSMQ rsmq;

@BindToRegistry
public Processor rsmqSend() {
		return exchange -> {
			String channel = (String)exchange.getProperty("channel");			
			String message = (String)exchange.getIn().getBody();

			String id = rsmq.sendMessage()
				.qname(channel)
				.message(message)
				.exec();
		};
}

