@BindToRegistry
public Processor sendEmail() {
    return exchange -> {
        try {
            AmazonSimpleEmailService client = 
                AmazonSimpleEmailServiceClientBuilder.standard()
                .withRegion(Regions.US_EAST_2).build();

            String to = (String)exchange.getProperty("emailRecipient");
            String html = (String)exchange.getProperty("emailBodyHtml");
            String text = (String)exchange.getProperty("emailBodyText");
            String subject = (String)exchange.getProperty("emailSubject");
            String from = "no-reply@app.convey911.com";

            SendEmailRequest request = new SendEmailRequest()
                .withDestination(new Destination().withToAddresses(to))
                .withMessage(new Message()
                    .withBody(new Body()
                        .withHtml(new Content()
                            .withCharset("UTF-8").withData(html))
                        .withText(new Content()
                            .withCharset("UTF-8").withData(text)))
                    .withSubject(new Content()
                        .withCharset("UTF-8").withData(subject)))
                .withSource(from);
                // Comment or remove the next line if you are not using a
                // configuration set
                // .withConfigurationSetName(CONFIGSET);
            client.sendEmail(request);
            exchange.setProperty("emailSent", 1);
        } catch (Exception ex) {
            System.out.println("The email was not sent. Error message: " 
                + ex.getMessage());
        }
    };
}

